sp_widget.client_script:
api.controller = function ($rootScope) {
    /* widget controller */
    var c = this;
    c.data.items = $scope.data.items;
    console.log('aaa ' + c.data.items);

    c.openNav = function () {
        document.getElementById("myNav").style.width = "75%";
    };

    c.closeNav = function () {
        console.log('aaa');
        document.getElementById("myNav").style.width = "0%";
    };

    c.openTab = function (tabId, btnId) {
        var i;

        tabcontent = document.getElementsByClassName("tabcontent");

        for (i = 0; i < tabcontent.length; i++) {
            tabcontent[i].style.display = "none";
        }

        tab = document.getElementsByClassName("tablink");

        for (i = 0; i < tab.length; i++) {
            tab[i].classList.remove("active-tab");
        }

        document.getElementById(tabId).style.display = "block";
        document.getElementById(btnId).classList.add("active-tab")
    };

    $rootScope.$on('getExams', function(event, data) {
        // c.server.get({
        //     action: 'getExchangeRatesAccordingToCurrencyID',
        //     currencyID: $rootScope.currencyID
        // }).then(function(r) {
        //     c.data.exchangeRates = r.data.exchangeRates;
        //     c.data.currencyCode = $rootScope.currencyCode;
        // });
        c.data.test = $rootScope.test;
    });

    // $rootScope.test = 'vannnn';
    // $rootScope.$emit('getExams');
};

