sp_widget.client_script:
api.controller = function (
  $rootScope,
  $scope,
  $sce,
  spModal
) {
  /* widget controller */
  var c = this;

  c.openPopUp = function () {
    spModal.open({
       title: 'Give me a name',
			 widget: 'exams_new',
    });

  };







};

