sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var level = current.variables.level + '';
    var answerType = current.variables.answer_type + '';
    var subCat = current.variables.sub_category + '';
    var topicDetail = current.variables.topic + '';
    var correctAnswer = current.variables.correct_answer + '';
    var question = current.variables.question + '';
    var ANSWER_OPTION_1 = current.variables.answer_1 + '';
    var ANSWER_OPTION_2 = current.variables.answer_2 + '';
    var ANSWER_OPTION_3 = current.variables.answer_3 + '';
    var ANSWER_OPTION_4 = current.variables.answer_4 + '';
    var ANSWER_OPTION_5 = current.variables.answer_5 + '';
    var ANSWER_OPTION_6 = current.variables.answer_6 + '';
    var correctYesNo = current.variables.answer_7 + '';

    //import question bank table
    var grQuestion = new GlideRecord('x_292088_snow_exam_question_banks');

    grQuestion.level = level == 'fresher' ? 'fresher' : 'experienced';
    grQuestion.type = answerType == 'multiple_answers' ? 'Multiple Choice' : 'Yes/No';
//     grQuestion.sub_category = subCat;
    grQuestion.display_value = question;
    grQuestion.question = question;
    grQuestion.correct_answer = answerType == 'multiple_answers' ? correctAnswer : correctYesNo;
    grQuestion.topic_details = topicDetail ? topicDetail : '';

    newQuestion = grQuestion.insert();

    //import question key table
    if (newQuestion) {
        var grAnswer = new GlideRecord('x_292088_snow_exam_question_keys');
        grAnswer.question_id = newQuestion;

        if (answerType == 'multiple_answers') {
            grAnswer.option_a = ANSWER_OPTION_1 ? ANSWER_OPTION_1 : '';
            grAnswer.option_b = ANSWER_OPTION_2 ? ANSWER_OPTION_2 : '';
            grAnswer.option_c = ANSWER_OPTION_3 ? ANSWER_OPTION_3 : '';
            grAnswer.option_d = ANSWER_OPTION_4 ? ANSWER_OPTION_4 : '';
            grAnswer.option_e = ANSWER_OPTION_5 ? ANSWER_OPTION_5 : '';
            grAnswer.option_f = ANSWER_OPTION_6 ? ANSWER_OPTION_6 : '';
        } else {
            grAnswer.option_a = 'true';
            grAnswer.option_b = 'false';
        }

        grAnswer.insert();
    }

})(current, previous);
