sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var questionID = target.getUniqueValue();
    var answer = new GlideRecord('x_292088_snow_exam_question_keys');
    answer.addQuery('question_id', questionID);
    answer.query();
    if (answer.next()) {
        answer.option_a = source.u_a;
        answer.option_b = source.u_b;
        answer.option_c = source.u_c;
        answer.option_d = source.u_d;
		answer.option_e = source.u_e;
		answer.option_f = source.u_f;
        answer.update();
    } else {
        answer.option_a = source.u_a;
        answer.option_b = source.u_b;
        answer.option_c = source.u_c;
        answer.option_d = source.u_d;
		answer.option_e = source.u_e;
		answer.option_f = source.u_f;
		answer.question_id = questionID;
        answer.insert();
    }

})(source, map, log, target);
